<!-- scroll upwards -->
<script>
$(function(){
  var tickerLength = $('.scroll-hide .ul .li').length;
  var tickerHeight = $('.scroll-hide .ul .li').outerHeight();
  $('.scroll-hide .ul .li:last-child').prependTo('.scroll-hide .ul');
  $('.scroll-hide .ul').css('marginTop',-tickerHeight);
  function moveTop(){
    $('.scroll-hide .ul').animate({
      top : -tickerHeight
    },600, function(){
     $('.scroll-hide .ul .li:first-child').appendTo('.scroll-hide .ul');
      $('.scroll-hide .ul').css('top','');
    });
   }
  setInterval( function(){
    moveTop();
  }, 3000);
  });
</script>
<!-- timer -->
<script>
    function updateTimer() {
      future  = Date.parse("<?php echo $homeNotification->batch; ?> <?php echo $homeNotification->date; ?>, 2023 12:00:00");
      now     = new Date();
      diff    = future - now;

      days  = Math.floor( diff / (1000*60*60*24) );
      hours = Math.floor( diff / (1000*60*60) );
      mins  = Math.floor( diff / (1000*60) );
      secs  = Math.floor( diff / 1000 );

      d = days;
      h = hours - days  * 24;
      m = mins  - hours * 60;
      s = secs  - mins  * 60;

      document.getElementById("timer")
        .innerHTML =
          '<span style="font-size:11px !important;">' + d + '<span>d:</span></span>' +
          '<span style="font-size:11px !important;">' + m + '<span>m:</span></span>' +
          '<span style="font-size:11px !important;">' + s + '<span>s</span></span>';
    }
    setInterval('updateTimer()', 1000 );
</script>
